pub fn get_ca_key(rid: u64, ca_idx: u8) -> Option<rsa::RsaPublicKey> {
  match (rid, ca_idx) {
    // Visa
    (0xA000000003, 0x08) => Some(("03", "")),
    (0xA000000003, 0x09) => Some(("03", "")),
    // MasterCard
    (0xA000000004, 0x05) => Some(("03", "")),
    (0xA000000004, 0x06) => Some(("03", "")),
    // American Express
    (0xA000000025, 0x0F) => Some(("03", "")),
    (0xA000000025, 0x10) => Some(("03", "")),
    // https://web.archive.org/web/20250626191833/https://docs.worldpay.com/assets/pdf/emvnetworkkeys1.pdf
    // Discover
    (0xA000000152, 0x05) => Some(("03", "")),
    // JCB
    (0xA000000065, 0x14) => Some(("03", "")),
    // China UnionPay
    (0xA000000333, 0x04) => Some(("03", "")),
    // WEX
    (0xA000000768, 0xff) => Some(("03", "")),
    (0xA000000768, 0xf2) => Some(("03", "")),
    // Interac
    (0xA000000277, 0x08) => Some(("010001", "")),
    _ => None,
  }.map(|(exponent, modulus)| {
    rsa::RsaPublicKey::new(
      rsa::BigUint::from_bytes_be(&hex::decode(modulus).unwrap()),
      rsa::BigUint::from_bytes_be(&hex::decode(exponent).unwrap())
    ).unwrap()
  })
}
