use rsa::BigUint;

pub fn get_ca_key(rid: u64, ca_idx: u8) -> Option<rsa::RsaPublicKey> {
  match (rid, ca_idx) {
    (0xA000000003, 0x08) => Some(rsa::RsaPublicKey::new(rsa::BigUint::from_bytes_be(&hex::decode("").unwrap()), BigUint::from_bytes_be(&hex::decode("03").unwrap())).unwrap()),
    (0xA000000003, 0x09) => Some(rsa::RsaPublicKey::new(rsa::BigUint::from_bytes_be(&hex::decode("").unwrap()), BigUint::from_bytes_be(&hex::decode("03").unwrap())).unwrap()),
    (0xA000000004, 0x05) => Some(rsa::RsaPublicKey::new(rsa::BigUint::from_bytes_be(&hex::decode("").unwrap()), BigUint::from_bytes_be(&hex::decode("03").unwrap())).unwrap()),
    (0xA000000004, 0x06) => Some(rsa::RsaPublicKey::new(rsa::BigUint::from_bytes_be(&hex::decode("").unwrap()), BigUint::from_bytes_be(&hex::decode("03").unwrap())).unwrap()),
    (0xA000000025, 0x0F) => Some(rsa::RsaPublicKey::new(rsa::BigUint::from_bytes_be(&hex::decode("").unwrap()), BigUint::from_bytes_be(&hex::decode("03").unwrap())).unwrap()),
    (0xA000000025, 0x10) => Some(rsa::RsaPublicKey::new(rsa::BigUint::from_bytes_be(&hex::decode("").unwrap()), BigUint::from_bytes_be(&hex::decode("03").unwrap())).unwrap()),
    _ => None,
  }
}
